mapper.select:
select * from good,order_good
where order_good.order_id = #{order_id}
and order_good.good_id = good.good_id

mapper.update:
update good
set good_state = #{good_state},good_price = #{good_price}, good_origin = #{good_origin},
    good_weight = #{good_weight}, good_destination = #{good_destination},
    good_name = #{good_name}
where good_id = #{good_id}
