mapper.select:
select * from good,order_good
where order_good.order_id = #{order_id}
and order_good.good_id = good.good_id

mapper.update:
update good
set good_state = #{good_state},good_price = #{good_price},
    good_weight = #{good_weight},good_name = #{good_name}
where good_id = #{good_id}

mapper.delete:
delete from good
where good_id = (
    select good_id from(
        select good.good_id from good,order_good
        where order_good.order_id = #{order_id}
        and order_good.good_id = good.good_id
    ) as temp
)

mapper.select:
select * from good
where good_name = #{good_name}
and good_price = #{good_price}
and good_weight = #{good_weight}
