mapper.select:
select os.user_name consignor,os.user_phone consignor_phone,os.order_id, os.order_destination,os.order_origin,os.order_state, `user`.user_name consignee,`user`.user_phone consignee_phone, good.good_name from `user`,good,order_good,( select user_name,user_phone,consignee_id,order_id,order_origin,order_destination,order_state from `order`,`user` where consignor_id = user_id ) os where os.consignee_id = `user`.user_id and os.order_id = order_good.order_id and order_good.good_id = good.good_id
and ( os.user_name like concat('%', #{information}, '%') or os.user_phone like concat('%', #{information}, '%') or `user`.user_name like concat('%', #{information}, '%') or `user`.user_phone like concat('%', #{information}, '%') )
limit #{page},#{size}
